import React from "react";

const CheckColumn = (props) => {
  const yep1 = () => {
    props.setSwitch1(true);
  };
  const yep2 = () => {
    props.setSwitch2(true);
    console.log(`Switch 2 is ON`);
  };
  const yep3 = () => {
    props.setSwitch3(true);
    console.log(`Switch 3 is ON`);
  };

  const nope1 = () => {
    props.setSwitch1(false);
    console.log(`Switch 1 is OFF`);
  };

  const nope2 = () => {
    props.setSwitch2(false);
    console.log(`Switch 2 is OFF`);
  };

  const nope3 = () => {
    props.setSwitch3(false);
    console.log(`Switch 3 is OFF`);
  };

  return (
    <div className="CheckColumn">
      <button id="on1" onClick={yep1()}>
        ON
      </button>
      <button id="off1" onClick={nope1()}>
        OFF
      </button>
      <button id="on2" onClick={yep2()}>
        ON
      </button>
      <button id="off2" onClick={nope2()}>
        OFF
      </button>
      <button id="on3" onClick={yep3()}>
        ON
      </button>
      <button id="off3" onClick={nope3()}>
        OFF
      </button>
    </div>
  );
};

export default CheckColumn;
